import{_ as n,o as l,c as o,x as s,a as t,t as e,U as p}from"./chunks/framework.7f50b160.js";const F=JSON.parse('{"title":"安装MySQL","description":"","frontmatter":{"title":"安装MySQL"},"headers":[],"relativePath":"mysql/start/installData.md","lastUpdated":1681050577000}'),r={name:"mysql/start/installData.md"},c={id:"frontmatter-title",tabindex:"-1"},i=s("a",{class:"header-anchor",href:"#frontmatter-title","aria-label":'Permalink to "{{ $frontmatter.title }}"'},"​",-1),h=p(`<blockquote><p>声明: 操作系统：Ubuntu 22.04.02 MySQL 8.0</p></blockquote><h2 id="下载" tabindex="-1">下载 <a class="header-anchor" href="#下载" aria-label="Permalink to &quot;下载&quot;">​</a></h2><p>在安装任何软件的时候，我们都要在官方下载其源程序。请擦亮眼睛，一定要认准官方。 使用这个链接下载<a href="https://dev.mysql.com/downloads/repo/apt/" target="_blank" rel="noreferrer">官方源</a>，我们可以使用wget，进行下载。</p><div class="language-shell line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">shell</span><pre class="shiki github-dark"><code><span class="line"><span style="color:#B392F0;">sudo</span><span style="color:#E1E4E8;"> </span><span style="color:#9ECBFF;">wget</span><span style="color:#E1E4E8;"> </span><span style="color:#9ECBFF;">https://dev.mysql.com/get/mysql-apt-config_0.</span><span style="color:#79B8FF;">8.24</span><span style="color:#9ECBFF;">-1_all.deb</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><h2 id="安装官方源" tabindex="-1">安装官方源 <a class="header-anchor" href="#安装官方源" aria-label="Permalink to &quot;安装官方源&quot;">​</a></h2><p>使用一下命令</p><div class="language-shell line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">shell</span><pre class="shiki github-dark"><code><span class="line"><span style="color:#B392F0;">sudo</span><span style="color:#E1E4E8;"> </span><span style="color:#9ECBFF;">dpkg</span><span style="color:#E1E4E8;"> </span><span style="color:#79B8FF;">-i</span><span style="color:#E1E4E8;"> </span><span style="color:#9ECBFF;">mysql-apt-config_0.</span><span style="color:#79B8FF;">8.24</span><span style="color:#9ECBFF;">-1_all.ded</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><p>在安装的过程中会出现安装界面，根据我的截图操作就可以了</p><details class="details custom-block"><summary>安装源界面</summary><p><img src="https://photohosting.oss-cn-hangzhou.aliyuncs.com/captures/mysql/QQ20230317-124813.png" alt=""></p><p><img src="https://photohosting.oss-cn-hangzhou.aliyuncs.com/captures/mysql/QQ20230317-124945.png" alt=""></p><p><img src="https://photohosting.oss-cn-hangzhou.aliyuncs.com/captures/mysql/QQ20230317-125104.png" alt=""></p></details><p>等待它安装完就好了。不过它会有一个警告。我们不需要管他，直接下一步。</p><details class="details custom-block"><summary>安装警告</summary><p><img src="https://photohosting.oss-cn-hangzhou.aliyuncs.com/captures/mysql/QQ20230317-125220.png" alt=""></p></details><h2 id="更新系统源" tabindex="-1">更新系统源 <a class="header-anchor" href="#更新系统源" aria-label="Permalink to &quot;更新系统源&quot;">​</a></h2><div class="language-shell line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">shell</span><pre class="shiki github-dark"><code><span class="line"><span style="color:#B392F0;">sudo</span><span style="color:#E1E4E8;"> </span><span style="color:#9ECBFF;">apt</span><span style="color:#E1E4E8;"> </span><span style="color:#9ECBFF;">update</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><h2 id="安装" tabindex="-1">安装 <a class="header-anchor" href="#安装" aria-label="Permalink to &quot;安装&quot;">​</a></h2><div class="language- line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki github-dark"><code><span class="line"><span style="color:#e1e4e8;">sudo apt install mysql-server</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><p><img src="https://photohosting.oss-cn-hangzhou.aliyuncs.com/captures/mysql/QQ20230317-125452.png" alt="直接yes"> 在终端中键入命令后-&gt;回车，会出现一个安装界面 这个时候需要输入root密码</p><p><img src="https://photohosting.oss-cn-hangzhou.aliyuncs.com/captures/mysql/QQ20230317-125605.png" alt=""></p><p>确认密码 <img src="https://photohosting.oss-cn-hangzhou.aliyuncs.com/captures/mysql/QQ20230317-125719.png" alt=""></p><p>在这个界面中直接 OK 就好 <img src="https://photohosting.oss-cn-hangzhou.aliyuncs.com/captures/mysql/QQ20230317-125804.png" alt=""></p><p>因为 mysql 8.0 更改了认证方式，为了兼容老系统，我们选择老的认证方式 <img src="https://photohosting.oss-cn-hangzhou.aliyuncs.com/captures/mysql/QQ20230317-125927.png" alt=""></p><p>回车后，等待其安装完成 <img src="https://photohosting.oss-cn-hangzhou.aliyuncs.com/captures/mysql/QQ20230317-130330.png" alt=""></p><h2 id="检查是否安装成功" tabindex="-1">检查是否安装成功 <a class="header-anchor" href="#检查是否安装成功" aria-label="Permalink to &quot;检查是否安装成功&quot;">​</a></h2><div class="language-shell line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">shell</span><pre class="shiki github-dark"><code><span class="line"><span style="color:#B392F0;">sudo</span><span style="color:#E1E4E8;"> </span><span style="color:#9ECBFF;">systemctl</span><span style="color:#E1E4E8;"> </span><span style="color:#9ECBFF;">status</span><span style="color:#E1E4E8;"> </span><span style="color:#9ECBFF;">mysql</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><p>如果在终端中打印了一下信息，说明安装成功了 <img src="https://photohosting.oss-cn-hangzhou.aliyuncs.com/captures/mysql/QQ20230317-130423.png" alt=""></p><h2 id="登陆到-mysql" tabindex="-1">登陆到 MySQL <a class="header-anchor" href="#登陆到-mysql" aria-label="Permalink to &quot;登陆到 MySQL&quot;">​</a></h2><div class="language-shell line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">shell</span><pre class="shiki github-dark"><code><span class="line"><span style="color:#B392F0;">mysql</span><span style="color:#E1E4E8;"> </span><span style="color:#79B8FF;">-u</span><span style="color:#E1E4E8;"> </span><span style="color:#9ECBFF;">root</span><span style="color:#E1E4E8;"> </span><span style="color:#79B8FF;">-p</span><span style="color:#E1E4E8;"> </span><span style="color:#F97583;">&lt;</span><span style="color:#9ECBFF;">输入你的密</span><span style="color:#E1E4E8;">码</span><span style="color:#F97583;">&gt;</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><p>出现一下内容，说明你的MySQL 就已经可以使用了 <img src="https://photohosting.oss-cn-hangzhou.aliyuncs.com/captures/mysql/QQ20230317-130521.png" alt=""></p><h2 id="开启远程登陆设置" tabindex="-1">开启远程登陆设置 <a class="header-anchor" href="#开启远程登陆设置" aria-label="Permalink to &quot;开启远程登陆设置&quot;">​</a></h2><p>默认情况下，MySQL只能在本地登陆，为了方便操作，我们要吧root的host改为任意</p><div class="language-sql line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">sql</span><pre class="shiki github-dark"><code><span class="line"><span style="color:#E1E4E8;">show databases;</span></span>
<span class="line"></span>
<span class="line"><span style="color:#F97583;">use</span><span style="color:#E1E4E8;"> mysql;</span></span>
<span class="line"></span>
<span class="line"><span style="color:#F97583;">select</span><span style="color:#E1E4E8;"> host, user </span><span style="color:#F97583;">from</span><span style="color:#E1E4E8;"> user; # 查看 </span><span style="color:#F97583;">root</span><span style="color:#E1E4E8;"> 的 host</span></span>
<span class="line"></span>
<span class="line"><span style="color:#F97583;">update</span><span style="color:#E1E4E8;"> user </span><span style="color:#F97583;">set</span><span style="color:#E1E4E8;"> host</span><span style="color:#F97583;">=</span><span style="color:#9ECBFF;">&quot;%&quot;</span><span style="color:#E1E4E8;"> </span><span style="color:#F97583;">where</span><span style="color:#E1E4E8;"> user</span><span style="color:#F97583;">=</span><span style="color:#9ECBFF;">&quot;root&quot;</span><span style="color:#E1E4E8;">; # 设置root用户</span></span>
<span class="line"></span>
<span class="line"><span style="color:#E1E4E8;">flush privileges; # 更新 MySQL</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br><span class="line-number">5</span><br><span class="line-number">6</span><br><span class="line-number">7</span><br><span class="line-number">8</span><br><span class="line-number">9</span><br></div></div><details class="details custom-block"><summary>更改 root 截图</summary><p><img src="https://photohosting.oss-cn-hangzhou.aliyuncs.com/captures/mysql/QQ20230317-130627.png" alt=""><img src="https://photohosting.oss-cn-hangzhou.aliyuncs.com/captures/mysql/QQ20230317-130737.png" alt=""><img src="https://photohosting.oss-cn-hangzhou.aliyuncs.com/captures/mysql/QQ20230317-130907.png" alt=""><img src="https://photohosting.oss-cn-hangzhou.aliyuncs.com/captures/mysql/QQ20230317-131123.png" alt=""><img src="https://photohosting.oss-cn-hangzhou.aliyuncs.com/captures/mysql/QQ20230317-131455.png" alt=""><img src="https://photohosting.oss-cn-hangzhou.aliyuncs.com/captures/mysql/QQ20230317-131352.png" alt=""></p></details>`,31);function u(a,d,y,m,g,E){return l(),o("div",null,[s("h1",c,[t(e(a.$frontmatter.title)+" ",1),i]),h])}const q=n(r,[["render",u]]);export{F as __pageData,q as default};
